package ejercicios01;

public class Bucles02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1 = 10;
		int num2 = 10;
		
		while (num1 >= 1) {
			System.out.println(num1);
			num1--;
		}
		
		System.out.println(" - - - - ");
		
		for (int i = 1; num2 >= i; num2--) {
			System.out.println(num2);
		}
	}

}
