package ejercicios01;

public class Bucles01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1 = 1;
		int num2 = 1;
		
		while (num1 <= 10) {
			System.out.println(num1);
			num1++;
		}
		
		System.out.println(" - - - - ");
		
		for (int i = 10; num2 <= i; num2++) {
			System.out.println(num2);
		}
		
	}

}
