package ejercicios01;

public class Bucles07 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int numero = 30;
		int suma = 0;
		
		while (numero <= 50) {
			System.out.println(numero);
			suma = suma + numero;
			numero++;
		}
		System.out.println("la suma total entre 30 - 50 = " + suma);
		
	}

}
